import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = () => {
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 flex justify-around py-2">
      <Link to="/" className="flex flex-col items-center justify-center p-2">
        <div className={`w-8 h-8 flex items-center justify-center rounded-full ${isActive('/') ? 'bg-purple-100' : ''}`}>
          <i className={`fas fa-home ${isActive('/') ? 'text-purple-500' : 'text-gray-500'}`}></i>
        </div>
        <span className={`text-xs mt-1 ${isActive('/') ? 'text-purple-500' : 'text-gray-500'}`}>Home</span>
      </Link>

      <Link to="/notes" className="flex flex-col items-center justify-center p-2">
        <div className={`w-8 h-8 flex items-center justify-center rounded-full ${isActive('/notes') ? 'bg-purple-100' : ''}`}>
          <i className={`fas fa-book ${isActive('/notes') ? 'text-purple-500' : 'text-gray-500'}`}></i>
        </div>
        <span className={`text-xs mt-1 ${isActive('/notes') ? 'text-purple-500' : 'text-gray-500'}`}>Notes</span>
      </Link>

      <Link to="/lectures" className="flex flex-col items-center justify-center p-2">
        <div className={`w-8 h-8 flex items-center justify-center rounded-full ${isActive('/lectures') ? 'bg-purple-100' : ''}`}>
          <i className={`fas fa-video ${isActive('/lectures') ? 'text-purple-500' : 'text-gray-500'}`}></i>
        </div>
        <span className={`text-xs mt-1 ${isActive('/lectures') ? 'text-purple-500' : 'text-gray-500'}`}>Lectures</span>
      </Link>
    </div>
  );
};

export default Navigation;
