import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = () => {
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  return (
    <div className="bottom-navigation">
      <Link to="/" className={`nav-button ${isActive('/') ? 'active' : ''}`}>
        <i className="fas fa-home"></i>
        <span>Home</span>
      </Link>
      <Link to="/notes" className={`nav-button ${isActive('/notes') ? 'active' : ''}`}>
        <i className="fas fa-book"></i>
        <span>Notes</span>
      </Link>
      <Link to="/lectures" className={`nav-button ${isActive('/lectures') ? 'active' : ''}`}>
        <i className="fas fa-play-circle"></i>
        <span>Lectures</span>
      </Link>
      <Link to="/quiz" className={`nav-button ${isActive('/quiz') ? 'active' : ''}`}>
        <i className="fas fa-question-circle"></i>
        <span>Quiz</span>
      </Link>
      <Link to="/focus" className={`nav-button ${isActive('/focus') ? 'active' : ''}`}>
        <i className="fas fa-clock"></i>
        <span>Focus</span>
      </Link>
    </div>
  );
};

export default Navigation;
